package com.array;

public class SumOfArrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] arr= {1,2,3,4,5,6,7,8,9};
		
		int total=0;
		
		for(int i=0;i<arr.length;i++) {
			
			total+=arr[i];
			
		}
			
			System.out.println("The total of array is "+total);
			
		
		

	}

}
