package bright.InterviewQuestion;

public class loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int i=40;
  while(i<=100){
	  System.out.println("increment i :" +i);
	  ++i;
  }
	  /*decrement*/
	  
	  int a=50;
	  while(a>=10){
		  System.out.println("dec a" +a);
		  --a;
	  }
  }
	}


